    public static partial class Prelude
    {
        // Zip() is new to .Net 4. (unlike the rest of LINQ, which was introduced in .Net 3.5)
        public static IEnumerable<TResult> Zip<T1, T2, TResult>(this IEnumerable<T1> seqA, IEnumerable<T2> seqB, Func<T1, T2, TResult> func)
        {
            if (seqA == null) throw new ArgumentNullException(nameof(seqA));
            if (seqB == null) throw new ArgumentNullException(nameof(seqB));

            using (var iteratorA = seqA.GetEnumerator())
            using (var iteratorB = seqB.GetEnumerator())
            {
                while (iteratorA.MoveNext() && iteratorB.MoveNext())
                {
                    yield return func(iteratorA.Current, iteratorB.Current);
                }
            }
        }
    }

